;(function($){
	if( typeof $ == 'undefined' ) {
		console.log('Not jQuery');
		return ;
	}
	
	var g_i = 0,
		imgs = [];
	
	$(document).ready(function(){
		$('body').addClass('loaded');
		
		if( $('body').getAttribute('data-option') ) 
			console.log(this);
		
		
		var elements = $('.elements');
		
		if( !elements.hasClass('loadmore') ){
			elements.addClass('loadmore');
			
			elements.addClass('g_i_'+g_i);
			g_i++;
			
		} else {
			elements.removeClass('loadmore');
			
		}
		
		// using global
		if( imgs.indexOf(this.href) < 0   )
			imgs.push(this.href);
		
		
	});
	
})(jQuery);